import React, { useState } from "react";
import Web3 from "web3";
import { ContractABI } from "./ContractABI";

import "./App.css";

const web3 = new Web3(new Web3.providers.HttpProvider("HTTP://127.0.0.1:7545"));
web3.eth.defaultAccount = web3.eth.accounts[0];
var RemixContract = new web3.eth.Contract(ContractABI, "");
var prize = 0;

function App() {
  const [message, setMessage] = useState("");
  const [fin, setFin] = useState("");
  const [headMessage, setHeadMessage] = useState("");

  const getContract = async e => {
    e.preventDefault();

    setHeadMessage(message);

    RemixContract = new web3.eth.Contract(ContractABI, message);

    var ress = await RemixContract.methods
      .getParticipantsAccn()
      .call();

    prize = await RemixContract.methods
      .getPrize()
      .call();

    setFin(ress.map((idx) => <li>{idx}</li>))

  };

  const getParticipants = async e => {
    var participants = await RemixContract.methods
      .getParticipantsAccn()
      .call();

    setFin(participants.map((idx) => <li>{idx}</li>))
  };

  const register = async e => {
    e.preventDefault();

    const accounts = await window.ethereum.enable();
    const account = accounts[0];

    const gas = await RemixContract.methods.enter(message).estimateGas();
    const result = await RemixContract.methods
      .enter(message)
      .send({ from: account, gas });

    console.log(result);
  }

  return (
    <div className="App" >
      <header className="App-header">

        <div className="splitScreen" >


          {/* Partea in care afisam informatii despre contract */}
          <div className="topPane">
            <form onSubmit={getContract} >
              <label>
                <input
                  placeHolder="adresa contract"
                  type="text"
                  name="message"
                  value={message}
                  onChange={e => setMessage(e.target.value)}
                />
              </label>
              <input type="submit" value="Cauta" />
            </form>
            <div> {headMessage} : premiu de ! {prize} </div>
          </div>

          <div className="secondTopPane">

          <form onSubmit={register}>
              <label>
                <input
                  placeHolder="email"
                  className="smallBox"
                  type="text"
                  name="message"
                  value={message}
                  onChange={e => setMessage(e.target.value)}
                />
              </label>
              <input type="submit" value="Inscriere" className="smallBox"/>
            </form>

            <button onClick={getParticipants} type="button">
              Arata participantii
            </button>
            <ul> {fin} </ul>

          </div>




          {/*partea in care apare optiunea de a crea un nou contract*/}
          <div className="bottomPane">

          </div>


        </div>
      </header>
    </div>
  );
}

export default App;
