import React, { useState } from "react";
import Web3 from "web3";
import { ContractABI } from "./ContractABI";

import "./App.css";

const web3 = new Web3(new Web3.providers.HttpProvider("HTTP://127.0.0.1:7545"));
web3.eth.defaultAccount = web3.eth.accounts[0];
var RemixContract = new web3.eth.Contract(ContractABI,"0xa97A13f227874221fD42ce7f1C4FE97D8346d446");
var prize = 0;

function App() {
  const [message,setMessage] = useState("0x829776E91f6BD1F0E4d3208C90B004CFe9DAfb1A");
  const [fin, setFin] = useState("");

  const getContract = async e => {
    e.preventDefault();

    RemixContract = new web3.eth.Contract(ContractABI,message);
    console.log(message)

    var ress = await RemixContract.methods
    .getBetters()
    .call();

    prize = await RemixContract.methods
    .getPrizeSize()
    .call();



    console.log(ress);
    console.log(prize);

    setFin( ress.map( (idx) => <li>{idx}</li> ) )

 };

  const getData = async e => {
    var ress = await RemixContract.methods
      .getBetters()
      .call();

    console.log(ress)
    setFin( ress.map( (idx) => <li>{idx}</li> ) )
  };

  const register = async e => {
    e.preventDefault();

    const accounts = await window.ethereum.enable();
    const account = accounts[0];

    const gas = await RemixContract.methods.enter(message).estimateGas();
    const result = await RemixContract.methods
      .enter(message)
      .send({ from: account, gas });

      console.log(result);
  }

  return (
    <div className="App" style="display:flex; flexDirection:row;" >
        <header className="App-header">

        <div style="width:50%;" >

            <div>Contractul curent este : {message} </div>
            <h1>Premiu de ! {prize} </h1>

            <form onSubmit={getContract}>
              <label>
                Set Message:
                <input
                  type="text"
                  name="message"
                  value={message}
                  onChange={e => setMessage(e.target.value)}
                />
              </label>
              <input type="submit" value="Set Message" />
            </form>
        </div>

        <div style="width:50%;">
          <button onClick={getData} type="button">
            Get Message
          </button>

          <button onClick={register} type="button">
            register
          </button>

          <ul> { fin } </ul>
        </div>
        </header>


    </div>

  );
}

export default App;
