import React, { useState } from "react";
import Web3 from "web3";
import { ContractABI } from "./ContractABI";

import "./App.css";

const web3 = new Web3(new Web3.providers.HttpProvider("HTTP://127.0.0.1:7545"));
web3.eth.defaultAccount = web3.eth.accounts[0];

const RemixContract = new web3.eth.Contract(
  ContractABI,
  "0xEb2397e5E022bBdc1AA6e304d988ccFdF08E7A9C"
);

function App() {
  const [message, setMessage, lista] = useState("");

  const setData = async e => {

    e.preventDefault();

    const accounts = await window.ethereum.enable();
    const account = accounts[0];

    console.log(accounts)

    const gas = await RemixContract.methods.setMessage(message).estimateGas();
    const result = await RemixContract.methods
      .setMessage(message)
      .send({ from: account, gas });

    console.log(result);
  };

  const getDefaultData = async e => {
    RemixContract.methods
      .defaultMessage()
      .call()
      .then(console.log);
  };

  const getData = async e => {
    RemixContract.methods
      .getMessage()
      .call()
      .then( ses => {
        return this.setState({lista:ses})
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={setData}>
          <label>
            Set Message:
            <input
              type="text"
              name="message"
              value={message}
              onChange={e => setMessage(e.target.value)}
            />
          </label>
          <input type="submit" value="Set Message" />
        </form>
        <br />
        <button onClick={getData} type="button">
          Get Message
        </button>
        <button onClick={getDefaultData} type="button">
          Get Default Message
        </button>
        <div>
          <li>{lista}</li>
        </div>
      </header>
    </div>
  );
}

export default App;
