import React, { useState } from "react";
import Web3 from "web3";
import { ContractABI } from "./ContractABI";

import "./App.css";

const web3 = new Web3(new Web3.providers.HttpProvider("HTTP://127.0.0.1:7545"));
web3.eth.defaultAccount = web3.eth.accounts[0];
var RemixContract = new web3.eth.Contract(ContractABI,"0xa97A13f227874221fD42ce7f1C4FE97D8346d446");

function App() {
  const [message,setMessage] = useState("");
  const [fin, setFin] = useState("");

  const getContract = async e => {
    e.preventDefault();

    RemixContract = new web3.eth.Contract(ContractABI,message);
    console.log(message)

    var ress = await RemixContract.methods
    .getBetters()
    .call();

    console.log(ress)
    setFin( ress.map( (idx) => <li>{idx}</li> ) )

 };

  const getData = async e => {
    var ress = await RemixContract.methods
      .getBetters()
      .call();

    console.log(ress)
    setFin( ress.map( (idx) => <li>{idx}</li> ) )
  };

  const register = async e => {
    e.preventDefault();

    const accounts = await window.ethereum.enable();
    const account = accounts[0];

    const gas = await RemixContract.methods.setMessage(message).estimateGas();
    const result = await RemixContract.methods
      .setMessage(message)
      .send({ from: account, gas });

      console.log(result);

  }

  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={getContract}>
          <label>
            Set Message:
            <input
              type="text"
              name="message"
              value={message}
              onChange={e => setMessage(e.target.value)}
            />
          </label>
          <input type="submit" value="Set Message" />
        </form>


        <br />
        <button onClick={getData} type="button">
          Get Message
        </button>

        <button onClick={register} type="button">
          Get Message
        </button>

        <ul> { fin } </ul>
      </header>
    </div>
  );
}

export default App;
