import React, { useState } from "react";
import Web3 from "web3";
import { ContractABI } from "./ContractABI";

import "./App.css";

const web3 = new Web3(new Web3.providers.HttpProvider("HTTP://127.0.0.1:7545"));
web3.eth.defaultAccount = web3.eth.accounts[0];
const RemixContract = new web3.eth.Contract(ContractABI,"0xa97A13f227874221fD42ce7f1C4FE97D8346d446") 

function App() {
  const [message,setMessage] = useState("");
  const [fin, setFin] = useState("");
  /*const [RemixContract, setContract ] = useState("");

  setContract( 
    new web3.eth.Contract(ContractABI,"0xa97A13f227874221fD42ce7f1C4FE97D8346d446") 
  );
  */
  const getContract = async e => {
    new web3.eth.Contract(ContractABI,message);
    var ress = await RemixContract.methods
    .getBetters()
    .call();

    console.log(ress)
    setFin( ress.map( (idx) => <li>{idx}</li> ) )
  };

  const getData = async e => {
    var ress = await RemixContract.methods
      .getBetters()
      .call();

    console.log(ress)
    setFin( ress.map( (idx) => <li>{idx}</li> ) )
  };

  return (
    <div className="App">
      <header className="App-header">



        <br />
        <button onClick={getData} type="button">
          Get Message
        </button>


        <ul> { fin } </ul>
      </header>
    </div>
  );
}

export default App;
